window.addEventListener("DOMContentLoaded",function(){var d=["January","February","March","April","May","June","July","August","September","October","November","December"];var b=document.getElementsByClassName("fixed-date");var a=b.length;for(var e=0;e<a;e++){var c=new Date(b[e].innerText+" UTC");b[e].innerText=d[c.getMonth()]+" "+c.getDate()+", "+c.getFullYear()+", "+c.getHours()+":"+("0"+c.getMinutes()).slice(-2)+":"+("0"+c.getSeconds()).slice(-2)}});